import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import StatusIndicator from "./StatusIndicator";
import { User } from "@shared/schema";
import { GraduationCap, Sun, Moon } from "lucide-react";
import { useEffect, useState } from "react";

type StatusType = "studying" | "free" | "help" | "busy" | "tired" | "social";

interface HeaderProps {
  user: User | null;
  userStatus?: StatusType;
  onLogin?: () => void;
  onLogout?: () => void;
}

export default function Header({ user, userStatus = "free", onLogin, onLogout }: HeaderProps) {
  const [isDark, setIsDark] = useState(false);

  // Initialize theme on mount
  useEffect(() => {
    const savedTheme = localStorage.getItem('theme');
    const prefersDark = window.matchMedia('(prefers-color-scheme: dark)').matches;
    const shouldBeDark = savedTheme === 'dark' || (!savedTheme && prefersDark);

    setIsDark(shouldBeDark);
    document.documentElement.classList.toggle('dark', shouldBeDark);
  }, []);

  const toggleTheme = () => {
    const newIsDark = !isDark;
    setIsDark(newIsDark);
    document.documentElement.classList.toggle('dark', newIsDark);
    localStorage.setItem('theme', newIsDark ? 'dark' : 'light');
  };

  const userInitials = user?.username
    .split(" ")
    .map(n => n[0])
    .join("")
    .toUpperCase();

  return (
    <header className="sticky top-0 z-50 w-full border-b bg-background/95 backdrop-blur supports-[backdrop-filter]:bg-background/60">
      <div className="container mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex h-16 items-center justify-between">
          {/* Logo */}
          <div className="flex items-center gap-2">
            <GraduationCap className="h-6 w-6 text-primary" />
            <h1 className="text-lg font-semibold">Campus Connect</h1>
          </div>

          {/* User section */}
          <div className="flex items-center gap-4">
            {/* Theme toggle */}
            <Button
              variant="ghost"
              size="icon"
              onClick={toggleTheme}
              data-testid="theme-toggle"
            >
              {isDark ? (
                <Sun className="h-4 w-4" />
              ) : (
                <Moon className="h-4 w-4" />
              )}
            </Button>

            {user ? (
              <div className="flex items-center gap-3">
                {/* User status */}
                <StatusIndicator status={userStatus} size="sm" />

                {/* User info */}
                <div className="flex items-center gap-2">
                  <Avatar className="h-8 w-8">
                    {user.avatar && <AvatarImage src={user.avatar} alt={user.username} />}
                    <AvatarFallback className="text-xs">{userInitials}</AvatarFallback>
                  </Avatar>

                  <div className="hidden sm:block">
                    <p className="text-sm font-medium">{user.username}</p>
                    <p className="text-xs text-muted-foreground">{user.major}</p>
                  </div>
                </div>

                <Button 
                  variant="outline" 
                  size="sm" 
                  onClick={onLogout}
                  data-testid="logout-button"
                >
                  Logout
                </Button>
              </div>
            ) : (
              <Button onClick={onLogin} data-testid="login-button">
                Login
              </Button>
            )}
          </div>
        </div>
      </div>
    </header>
  );
}

client/src/components/ScheduleForm.tsx.content:
import { useState, useRef } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Badge } from "@/components/ui/badge";
import { insertScheduleSchema, InsertSchedule, Schedule } from "@shared/schema";
import { Plus, X, Clock, Upload } from "lucide-react";
import { useToast } from "@/hooks/use-toast";

interface ScheduleFormProps {
  schedules: Schedule[];
  onAddSchedule: (schedule: InsertSchedule) => void;
  onRemoveSchedule?: (scheduleId: string) => void;
  onUploadICS?: (schedules: Schedule[]) => void;
  userId?: string;
}

const days = [
  { value: "monday", label: "Monday" },
  { value: "tuesday", label: "Tuesday" },
  { value: "wednesday", label: "Wednesday" },
  { value: "thursday", label: "Thursday" },
  { value: "friday", label: "Friday" },
  { value: "saturday", label: "Saturday" },
  { value: "sunday", label: "Sunday" },
];

export default function ScheduleForm({ schedules, onAddSchedule, onRemoveSchedule, onUploadICS, userId }: ScheduleFormProps) {
  const [isFormOpen, setIsFormOpen] = useState(false);
  const [isUploading, setIsUploading] = useState(false);
  const fileInputRef = useRef<HTMLInputElement>(null);
  const { toast } = useToast();

  const form = useForm<InsertSchedule>({
    resolver: zodResolver(insertScheduleSchema),
    defaultValues: {
      courseCode: "",
      courseName: "",
      day: "monday" as const,
      startTime: "",
      endTime: "",
      location: "",
    },
  });

  const onSubmit = (data: InsertSchedule) => {
    onAddSchedule(data);
    form.reset();
    setIsFormOpen(false);
    console.log('Schedule added:', data);
  };

  const formatTime = (time: string) => {
    const [hours, minutes] = time.split(':');
    const hour = parseInt(hours);
    const ampm = hour >= 12 ? 'PM' : 'AM';
    const displayHour = hour === 0 ? 12 : hour > 12 ? hour - 12 : hour;
    return `${displayHour}:${minutes} ${ampm}`;
  };

  const handleFileUpload = async (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (!file || !userId) return;

    if (!file.name.endsWith('.ics')) {
      toast({
        title: "Invalid file type",
        description: "Please select a .ics calendar file",
        variant: "destructive",
      });
      return;
    }

    setIsUploading(true);
    const formData = new FormData();
    formData.append('icsFile', file);

    try {
      const response = await fetch(`/api/schedules/${userId}/upload-ics`, {
        method: 'POST',
        body: formData,
      });

      const result = await response.json();

      if (response.ok) {
        toast({
          title: "Success!",
          description: result.message,
        });
        if (onUploadICS && result.schedules) {
          onUploadICS(result.schedules);
        }
      } else {
        toast({
          title: "Upload failed",
          description: result.error || "Failed to upload file",
          variant: "destructive",
        });
      }
    } catch (error) {
      toast({
        title: "Error",
        description: "An error occurred while uploading the file",
        variant: "destructive",
      });
    } finally {
      setIsUploading(false);
      if (fileInputRef.current) {
        fileInputRef.current.value = '';
      }
    }
  };

  const handleUploadClick = () => {
    fileInputRef.current?.click();
  };

  return (
    <Card>
      <CardHeader>
        <CardTitle className="flex items-center justify-between">
          <div className="flex items-center gap-2">
            <Clock className="h-5 w-5" />
            My Schedule
          </div>
          <div className="flex gap-2">
            <Button 
              size="sm" 
              variant="outline"
              onClick={handleUploadClick}
              disabled={isUploading || !userId}
              data-testid="upload-ics-button"
            >
              <Upload className="h-4 w-4 mr-1" />
              {isUploading ? "Uploading..." : "Import ICS"}
            </Button>
            <Button 
              size="sm" 
              onClick={() => setIsFormOpen(!isFormOpen)}
              data-testid="add-schedule-button"
            >
              <Plus className="h-4 w-4 mr-1" />
              Add Course
            </Button>
          </div>
        </CardTitle>
      </CardHeader>

      <CardContent className="space-y-4">
        {/* Hidden file input */}
        <input
          ref={fileInputRef}
          type="file"
          accept=".ics"
          onChange={handleFileUpload}
          style={{ display: 'none' }}
          data-testid="file-input-ics"
        />

        {/* Schedule List */}
        <div className="space-y-2">
          {schedules.length === 0 ? (
            <p className="text-sm text-muted-foreground text-center py-4">
              No courses added yet. Add your first course to get started!
            </p>
          ) : (
            schedules.map((schedule) => (
              <div 
                key={schedule.id} 
                className="flex items-center justify-between p-3 bg-accent/50 rounded-md"
                data-testid={`schedule-item-${schedule.id}`}
              >
                <div className="flex-1">
                  <div className="flex items-center gap-2">
                    <Badge variant="outline">{schedule.courseCode}</Badge>
                    <span className="font-medium text-sm">{schedule.courseName}</span>
                  </div>
                  <p className="text-xs text-muted-foreground">
                    {schedule.day.charAt(0).toUpperCase() + schedule.day.slice(1)} • {formatTime(schedule.startTime)} - {formatTime(schedule.endTime)}
                    {schedule.location && ` • ${schedule.location}`}
                  </p>
                </div>
                {onRemoveSchedule && (
                  <Button
                    size="sm"
                    variant="ghost"
                    onClick={() => onRemoveSchedule(schedule.id)}
                    data-testid={`remove-schedule-${schedule.id}`}
                  >
                    <X className="h-4 w-4" />
                  </Button>
                )}
              </div>
            ))
          )}
        </div>

        {/* Add Course Form */}
        {isFormOpen && (
          <Card>
            <CardContent className="p-4">
              <Form {...form}>
                <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
                  <div className="grid grid-cols-2 gap-4">
                    <FormField
                      control={form.control}
                      name="courseCode"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Course Code</FormLabel>
                          <FormControl>
                            <Input 
                              placeholder="CS 151" 
                              {...field} 
                              data-testid="input-course-code"
                            />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />

                    <FormField
                      control={form.control}
                      name="courseName"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Course Name</FormLabel>
                          <FormControl>
                            <Input 
                              placeholder="Intro to CS" 
                              {...field}
                              data-testid="input-course-name"
                            />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />
                  </div>

                  <FormField
                    control={form.control}
                    name="day"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Day</FormLabel>
                        <Select onValueChange={field.onChange} defaultValue={field.value}>
                          <FormControl>
                            <SelectTrigger data-testid="select-day">
                              <SelectValue placeholder="Select a day" />
                            </SelectTrigger>
                          </FormControl>
                          <SelectContent>
                            {days.map((day) => (
                              <SelectItem key={day.value} value={day.value}>
                                {day.label}
                              </SelectItem>
                            ))}
                          </SelectContent>
                        </Select>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <div className="grid grid-cols-2 gap-4">
                    <FormField
                      control={form.control}
                      name="startTime"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Start Time</FormLabel>
                          <FormControl>
                            <Input 
                              type="time" 
                              {...field}
                              data-testid="input-start-time"
                            />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />

                    <FormField
                      control={form.control}
                      name="endTime"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>End Time</FormLabel>
                          <FormControl>
                            <Input 
                              type="time" 
                              {...field}
                              data-testid="input-end-time"
                            />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />
                  </div>

                  <FormField
                    control={form.control}
                    name="location"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Location (Optional)</FormLabel>
                        <FormControl>
                          <Input 
                            placeholder="UC Building, Room 101" 
                            {...field}
                            value={field.value || ""}
                            data-testid="input-location"
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <div className="flex gap-2">
                    <Button 
                      type="submit" 
                      size="sm"
                      data-testid="submit-schedule"
                    >
                      Add Course
                    </Button>
                    <Button 
                      type="button" 
                      variant="outline" 
                      size="sm"
                      onClick={() => setIsFormOpen(false)}
                      data-testid="cancel-schedule"
                    >
                      Cancel
                    </Button>
                  </div>
                </form>
              </Form>
            </CardContent>
          </Card>
        )}
      </CardContent>
    </Card>
  );
}

client/src/components/StatusBoard.tsx.content:
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import UserCard from "./UserCard";
import { CurrentStatusResponse } from "@shared/schema";
import { Users, Clock, RefreshCw } from "lucide-react";
import { cn } from "@/lib/utils";

interface StatusBoardProps {
  statusData: CurrentStatusResponse | null;
  isLoading?: boolean;
  onRefresh?: () => void;
  onUserClick?: (userId: string) => void;
  className?: string;
}

export default function StatusBoard({ 
  statusData, 
  isLoading = false, 
  onRefresh,
  onUserClick,
  className 
}: StatusBoardProps) {
  const formatTime = (isoString: string) => {
    return new Date(isoString).toLocaleTimeString('en-US', {
      hour: 'numeric',
      minute: '2-digit',
      hour12: true
    });
  };

  if (isLoading && !statusData) {
    return (
      <div className={cn("grid md:grid-cols-2 gap-6", className)}>
        <Card>
          <CardHeader>
            <div className="animate-pulse h-6 bg-muted rounded w-32"></div>
          </CardHeader>
          <CardContent>
            <div className="space-y-3">
              {[1, 2, 3].map(i => (
                <div key={i} className="animate-pulse">
                  <div className="flex items-center gap-3 p-4 bg-muted/50 rounded-md">
                    <div className="h-10 w-10 bg-muted rounded-full"></div>
                    <div className="flex-1">
                      <div className="h-4 bg-muted rounded w-24 mb-2"></div>
                      <div className="h-3 bg-muted rounded w-16"></div>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </CardContent>
        </Card>
        <Card>
          <CardHeader>
            <div className="animate-pulse h-6 bg-muted rounded w-32"></div>
          </CardHeader>
          <CardContent>
            <div className="space-y-3">
              {[1, 2, 3].map(i => (
                <div key={i} className="animate-pulse">
                  <div className="flex items-center gap-3 p-4 bg-muted/50 rounded-md">
                    <div className="h-10 w-10 bg-muted rounded-full"></div>
                    <div className="flex-1">
                      <div className="h-4 bg-muted rounded w-24 mb-2"></div>
                      <div className="h-3 bg-muted rounded w-16"></div>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </CardContent>
        </Card>
      </div>
    );
  }

  return (
    <div className={cn("space-y-4", className)}>
      {/* Header with refresh */}
      <div className="flex items-center justify-between">
        <div className="flex items-center gap-2">
          <Clock className="h-5 w-5 text-muted-foreground" />
          <span className="text-sm text-muted-foreground">
            {statusData ? `Updated at ${formatTime(statusData.now)}` : 'No data'}
          </span>
        </div>
        <Button
          variant="outline"
          size="sm"
          onClick={onRefresh}
          disabled={isLoading}
          data-testid="refresh-status"
        >
          <RefreshCw className={cn("h-4 w-4 mr-2", isLoading && "animate-spin")} />
          Refresh
        </Button>
      </div>

      {/* Status boards */}
      <div className="grid md:grid-cols-2 gap-6">
        {/* In Class */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Users className="h-5 w-5 text-primary" />
              In Class ({statusData?.in_class.length || 0})
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-3" data-testid="in-class-list">
              {statusData?.in_class.length === 0 ? (
                <p className="text-sm text-muted-foreground text-center py-8">
                  No one is currently in class
                </p>
              ) : (
                statusData?.in_class.map((user) => (
                  <UserCard
                    key={user.id}
                    user={user}
                    status={user.manual_status as any}
                    currentClass={user.current_class}
                    onClick={() => onUserClick?.(user.id)}
                  />
                ))
              )}
            </div>
          </CardContent>
        </Card>

        {/* Free */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Users className="h-5 w-5 text-status-free" />
              Available ({statusData?.free.length || 0})
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-3" data-testid="free-list">
              {statusData?.free.length === 0 ? (
                <p className="text-sm text-muted-foreground text-center py-8">
                  No one is currently available
                </p>
              ) : (
                statusData?.free.map((user) => (
                  <UserCard
                    key={user.id}
                    user={user}
                    status={user.manual_status as any}
                    nextClass={user.next_class}
                    onClick={() => onUserClick?.(user.id)}
                  />
                ))
              )}
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

client/src/components/StatusButtons.tsx.content:
import { Button } from "@/components/ui/button";
import { cn } from "@/lib/utils";
import { 
  BookOpen, 
  Coffee, 
  Heart, 
  Clock, 
  Moon, 
  Users2 
} from "lucide-react";

type StatusType = "studying" | "free" | "help" | "busy" | "tired" | "social";

interface StatusButtonsProps {
  currentStatus: StatusType;
  onStatusChange: (status: StatusType) => void;
  className?: string;
}

const statusConfig: Record<StatusType, { 
  label: string; 
  icon: React.ElementType;
  color: string;
}> = {
  studying: { label: "Studying", icon: BookOpen, color: "text-status-studying" },
  free: { label: "Free", icon: Coffee, color: "text-status-free" },
  help: { label: "Can Help", icon: Heart, color: "text-status-help" },
  busy: { label: "Busy", icon: Clock, color: "text-status-busy" },
  tired: { label: "Tired", icon: Moon, color: "text-status-tired" },
  social: { label: "Social", icon: Users2, color: "text-status-social" },
};

export default function StatusButtons({ 
  currentStatus, 
  onStatusChange, 
  className 
}: StatusButtonsProps) {
  return (
    <div className={cn("grid grid-cols-2 md:grid-cols-3 gap-2", className)}>
      {Object.entries(statusConfig).map(([status, config]) => {
        const Icon = config.icon;
        const isActive = currentStatus === status;

        return (
          <Button
            key={status}
            variant={isActive ? "default" : "outline"}
            size="sm"
            onClick={() => onStatusChange(status as StatusType)}
            className={cn(
              "flex items-center gap-2 h-auto py-3",
              !isActive && "hover:bg-accent"
            )}
            data-testid={`status-button-${status}`}
          >
            <Icon className={cn(
              "h-4 w-4", 
              isActive ? "text-primary-foreground" : config.color
            )} />
            <span className="text-sm font-medium">{config.label}</span>
          </Button>
        );
      })}
    </div>
  );
}

client/src/components/StatusIndicator.ts.content:
import { Badge } from "@/components/ui/badge";
import { cn } from "@/lib/utils";

type StatusType = "studying" | "free" | "help" | "busy" | "tired" | "social";

interface StatusIndicatorProps {
  status: StatusType;
  size?: "sm" | "default" | "lg";
  className?: string;
}

const statusConfig: Record<StatusType, { label: string; color: string }> = {
  studying: { label: "Studying", color: "bg-status-studying text-white" },
  free: { label: "Free", color: "bg-status-free text-white" },
  help: { label: "Can Help", color: "bg-status-help text-white" },
